import React from 'react';
import axios from 'axios';
import get from 'lodash/get';
import { Select } from 'antd';
const { Option } = Select;
function Index(props) {
  return <div>你好世界</div>;
}

Index.getInitialProps = async () => {
  return Promise.resolve({ ts: 1 });
};

function getCategories() {
  return [
    {
      key: 'frontend',
      label: '前端'
    }
  ];
}

function getLangList() {
  return [
    {
      key: 'javascript',
      label: 'JavaScript'
    }
  ];
}

async function fetchGold(category = 'frontend') {
  const res = await axios.post(
    'https://extension-ms.juejin.im/resources/gold',
    {
      data: { category: 'frontend', order: 'heat', offset: 0, limit: 30 }
    }
  );
  console.log(res.data);
  return get(res, 'data.data');
}

async function fetchGithub(lang = 'javascript') {
  const res = await axios.post(
    'https://extension-ms.juejin.im/resources/github',
    {
      data: {
        category: 'trending',
        period: 'day',
        lang,
        offset: 0,
        limit: 30
      }
    }
  );
  console.log(res.data);
  return get(res, 'data.data');
}
export default Index;
