import React from 'react';
import axios from 'axios';
import Document from 'next/document';
import moment from 'moment';
import { Select, Row, Col } from 'antd';

function rem($px) {
  return `${(10 * $px) / 1280}rem`;
}

const { Option } = Select;
function Index(props) {
  const { goldNews = [], githubNews = [] } = props;
  return (
    <Document lang="zh-CN">
      <div className="wrap">
        <div className="gold-pane">
          <div className="filter">
            <img
              className="juejin-icon"
              src="data:image/svg+xml;base64,"
              alt="掘金"
            />
            <span className="juejin-font">掘金</span>
            <Select
              style={{ width: 120 }}
              defaultValue={getCategories.defaultValue}
            >
              {getCategories().map(({ key, label }) => (
                <Option key={key} value={key}>
                  {label}
                </Option>
              ))}
            </Select>
          </div>
          <div className="content">
            {goldNews.map(({ id, title, date, collectionCount, user }) => (
              <div key={id} className="gold-item">
                <div className="collectionCount">{collectionCount}</div>
                <div className="gold-info">
                  <div className="gold-title">{title}</div>
                  <div className="gold-footer">
                    <span className="date">
                      {moment(date.iso)
                        .fromNow()
                        .locale()}
                    </span>
                    <a href={user.url} className="username">
                      {user.username}
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="github-pane">
          <div className="filter">
            <img
              className="juejin-icon"
              src="https://e-gold-cdn.xitu.io/static/github.png?9140c37"
              alt="github"
            />
            <span className="juejin-font github-font">GitHub</span>
            <div className="lang-selects">
              <Select
                style={{ width: 120 }}
                defaultValue={getLangList.defaultValue}
              >
                {getLangList().map(({ key, label }) => (
                  <Option key={key} value={key}>
                    {label}
                  </Option>
                ))}
              </Select>
            </div>
          </div>
          <div className="content">
            <Row>
              {githubNews.map(
                ({
                  id,
                  url,
                  username,
                  reponame,
                  description,
                  starCount,
                  forkCount,
                  langColor,
                  lang
                }) => (
                  <Col key={id}>
                    <div className="title">
                      <a href={url}>
                        <span>{username}</span>
                        <span>/</span>
                        <span>{reponame}</span>
                      </a>
                    </div>
                    <div className="description">{description}</div>
                    <div className="footer">
                      <span>{starCount}</span>
                      <span>{forkCount}</span>
                      <span>
                        <span
                          className="lang-color"
                          style={{ backgroundColor: langColor }}
                        ></span>
                        {lang}
                      </span>
                    </div>
                  </Col>
                )
              )}
            </Row>
          </div>
        </div>
        <style jsx global>{`
          html {
            font-size: 10vw;
          }
          html body {
            height: 100%;
            background-color: #eceff1;
          }
          #__next {
            height: 100%;
          }
        `}</style>
        <style jsx>{`
          .lang-color {
            width: 20px;
            height: 20px;
            border-radius: 20px;
          }
          .juejin-icon {
            width: ${rem(30)};
            height: ${rem(30)};
            margin-right: ${rem(8)};
          }
          .juejin-font {
            margin-right: ${rem(6)};
            color: rgb(4, 74, 171);
            font-size: ${rem(16)};
          }
          .github-font {
            color: rgb(0, 0, 0);
          }
          .wrap {
            height: 100%;
            display: flex;
            overflow: hidden;
            padding: ${rem(20)} ${rem(18)} 0 ${rem(18)};
          }
          .gold-pane {
            width: ${rem(600)};
            height: 100%;
            overflow: auto;
            flex: none;
            display: flex;
            flex-direction: column;
            margin-right: ${rem(18)};
          }
          .github-pane {
            flex: auto;
          }
          .filter {
            flex: none;
            display: flex;
            align-items: center;
            padding: 0 ${rem(8)};
            height: ${rem(42)};
            background-color: #fff;
            border-radius: ${rem(2)};
            margin-bottom: ${rem(10)};
            margin-right: ${rem(12)};
          }
          .content {
            flex: auto;
            overflow: auto;
            padding-right: ${rem(12)};
          }
          .lang-selects {
            flex: auto;
            text-align: right;
          }
          .gold-item {
            background-color: #fff;
            border-radius: ${rem(2)};
            display: flex;
            align-items: center;
            padding: ${rem(6)} ${rem(5)};
          }
          .content > .gold-item + .gold-item {
            margin-top: ${rem(10)};
          }
          .collectionCount {
            background-color: #1683fc;
            color: #fff;
            height: ${rem(40)};
            width: ${rem(30)};
            border-radius: ${rem(2)};
            display: flex;
            align-items: center;
            justify-content: center;
            margin-right: ${rem(12)};
            flex: none;
          }
          .gold-info {
            flex: auto;
            min-width: 0;
          }
          .gold-title {
            color: #333;
            overflow: hidden;
            white-space: nowrap;
            text-overflow: ellipsis;
            line-height: 1.8;
            font-size: ${rem(14)};
          }
          .gold-footer {
            color: #c2c5cd;
            font-size: ${rem(12)};
          }
          .date {
            margin-right: ${rem(10)};
          }
          .username {
            color: #c2c5cd;
          }
        `}</style>
      </div>
    </Document>
  );
}

Index.getInitialProps = async () => {
  const [goldNews, githubNews] = await Promise.all([
    fetchGold(),
    fetchGithub()
  ]);
  return {
    goldNews,
    githubNews
  };
};

function getCategories() {
  return [
    {
      key: 'frontend',
      label: '前端'
    }
  ];
}
getCategories.defaultValue = 'frontend';

function getLangList() {
  return [
    {
      key: 'javascript',
      label: 'JavaScript'
    }
  ];
}
getLangList.defaultValue = 'javascript';

async function fetchGold(category = 'frontend') {
  const res = await axios.post(
    'https://extension-ms.juejin.im/resources/gold',
    {
      data: { category, order: 'heat', offset: 0, limit: 30 }
    }
  );
  return res.data.data;
}

async function fetchGithub(lang = 'javascript') {
  const res = await axios.post(
    'https://extension-ms.juejin.im/resources/github',
    {
      data: {
        category: 'trending',
        period: 'day',
        lang,
        offset: 0,
        limit: 30
      }
    }
  );
  return res.data.data;
}
export default Index;
