import React from 'react';
import axios from 'axios';
import get from 'lodash/get';
import { Select } from 'antd';
const { Option } = Select;
function Index(props) {
  return (
    <div>
      <div className="article-pane">
        <div className="filter">
          <img
            src="data:image/svg+xml;base64,"
            alt="掘金"
          />
          <span>掘金</span>
          <Select>
            {getCategories().map(({ key, label }) => (
              <Option key={key}>{label}</Option>
            ))}
            <Option></Option>
          </Select>
        </div>
      </div>
      <div className="github-pane">
        <div className="filter"></div>
      </div>
    </div>
  );
}

Index.getInitialProps = async () => {
  return Promise.resolve({ ts: 1 });
};

function getCategories() {
  return [
    {
      key: 'frontend',
      label: '前端'
    }
  ];
}

function getLangList() {
  return [
    {
      key: 'javascript',
      label: 'JavaScript'
    }
  ];
}

async function fetchGold(category = 'frontend') {
  const res = await axios.post(
    'https://extension-ms.juejin.im/resources/gold',
    {
      data: { category: 'frontend', order: 'heat', offset: 0, limit: 30 }
    }
  );
  console.log(res.data);
  return get(res, 'data.data');
}

async function fetchGithub(lang = 'javascript') {
  const res = await axios.post(
    'https://extension-ms.juejin.im/resources/github',
    {
      data: {
        category: 'trending',
        period: 'day',
        lang,
        offset: 0,
        limit: 30
      }
    }
  );
  console.log(res.data);
  return get(res, 'data.data');
}
export default Index;
