import React from 'react';
import axios from 'axios';
import get from 'lodash/get';
import { Select, Row, Col } from 'antd';
const { Option } = Select;
function Index(props) {
  const { goldNews, githubNews } = props;
  return (
    <div>
      <div className="gold-pane">
        <div className="filter">
          <img
            src="data:image/svg+xml;base64,"
            alt="掘金"
          />
          <span>掘金</span>
          <Select
            style={{ width: 120 }}
            defaultValue={getCategories.defaultValue}
          >
            {getCategories().map(({ key, label }) => (
              <Option key={key}>{label}</Option>
            ))}
            <Option></Option>
          </Select>
        </div>
        <div className="content">
          {goldNews.map(({ id, title, date, collectionCount, user }) => (
            <div key={id}>
              <div>{collectionCount}</div>
              <div>
                <div>{title}</div>
                <div>
                  <span>{date.iso.toString()}</span>
                  <a href={user.url}>{user.username}</a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="github-pane">
        <div className="filter">
          <img
            src="https://e-gold-cdn.xitu.io/static/github.png?9140c37"
            alt="github"
          />
          <span>GitHub</span>
          <Select>
            <Select
              style={{ width: 120 }}
              defaultValue={getLangList.defaultValue}
            >
              {getLangList().map(({ key, label }) => (
                <Option key={key}>{label}</Option>
              ))}
              <Option></Option>
            </Select>
          </Select>
        </div>
        <div className="content">
          <Row>
            {githubNews.map(
              ({ id, url, username, reponame, description, starCount }) => (
                <Col key={id}>
                  <div className="title">
                    <a href={url}>
                      <span>{username}</span>
                      <span>/</span>
                      <span>{reponame}</span>
                    </a>
                  </div>
                  <div className="description">{description}</div>
                  <div className="footer">
                    <span>{starCount}</span>
                    <span>{forkCount}</span>
                  </div>
                </Col>
              )
            )}
          </Row>
        </div>
      </div>
    </div>
  );
}

Index.getInitialProps = async () => {
  const [goldNews, githubNews] = await Promise.all(fetchGold(), fetchGithub());
  return {
    goldNews,
    githubNews
  };
};

function getCategories() {
  return [
    {
      key: 'frontend',
      label: '前端'
    }
  ];
}
getCategories.defaultValue = 'frontend';

function getLangList() {
  return [
    {
      key: 'javascript',
      label: 'JavaScript'
    }
  ];
}
getLangList.defaultValue = 'javascript';

async function fetchGold(category = 'frontend') {
  const res = await axios.post(
    'https://extension-ms.juejin.im/resources/gold',
    {
      data: { category: 'frontend', order: 'heat', offset: 0, limit: 30 }
    }
  );
  console.log(res.data);
  return get(res, 'data.data');
}

async function fetchGithub(lang = 'javascript') {
  const res = await axios.post(
    'https://extension-ms.juejin.im/resources/github',
    {
      data: {
        category: 'trending',
        period: 'day',
        lang,
        offset: 0,
        limit: 30
      }
    }
  );
  console.log(res.data);
  return get(res, 'data.data');
}
export default Index;
